mapper.select:
select rn, bno, btitle, bcontent, bwriter, bdate, hitcount
from(
	select rownum as rn, bno, btitle, bcontent, bwriter, bdate, hitcount
	from(
		select bno, btitle, bcontent, bwriter, bdate, hitcount
		from board
		order by bno asc
	)
	where rownum <= #{endRowNo} 
) 
where rn >= #{startRowNo}

select bno, btitle, bcontent, bwriter, bdate, hitcount
from board
where bno = #{bno}
